mapper.update:
update person
p_name = #{name}, p_pass = #{pass}, pl_id = #{placeId}, p_sex = #{sex}, p_time = #{enterTime}, p_born = #{born}, p_onduty = #{onDutyRate}, p_overtime = #{overTimeRate}
where p_id = #{id}

mapper.select:
select pl_salary
from wageNew.dbo.person,
     wageNew.dbo.place
where p_id = #{id}
  and person.pl_id = place.pl_id

select p_name
from dbo.person
where p_id = #{id}

select p_id
from person
where dbo.person.pl_id = #{placeId}
